mapper.select:
SELECT id, customer_id, date, item, problem, suggestion, amount, created_at, updated_at
FROM consumption_records

    customer_id = #{customerId}

        AND date >= #{startDate}


        AND date <= #{endDate}


ORDER BY date DESC, id DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM consumption_records

    customer_id = #{customerId}

        AND date >= #{startDate}


        AND date <= #{endDate}

SELECT id, customer_id, date, item, problem, suggestion, amount, created_at, updated_at
FROM consumption_records
WHERE id = #{id}
