mapper.select:
SELECT id, name, price, stock, image_url, description, created_at, updated_at FROM products
AND name LIKE CONCAT('%', #{search}, '%')
ORDER BY created_at DESC LIMIT #{offset}, #{pageSize}

SELECT id, name, price, stock, image_url, description, created_at, updated_at
FROM products
WHERE id = #{id}

mapper.update:
UPDATE products
SET stock = #{stock}
WHERE id = #{id}

UPDATE products
SET stock = stock - #{quantity}
WHERE id = #{id} AND stock >= #{quantity}

mapper.select:
SELECT COUNT(*)
FROM sale_items
WHERE product_id = #{productId}
