mapper.select:
SELECT id, username, password, nickname, avatar, created_at, updated_at
FROM users
WHERE username = #{username}

SELECT id, username, password, nickname, avatar, created_at, updated_at
FROM users
WHERE id = #{id}

SELECT id, username, password, nickname, avatar, created_at, updated_at FROM users
AND (username LIKE CONCAT('%', #{search}, '%') OR nickname LIKE CONCAT('%', #{search}, '%'))
ORDER BY id DESC LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE users
username = #{username}, password = #{password}, nickname = #{nickname}, avatar = #{avatar},
WHERE id = #{id}
